import './App.css';
// import Attribution from './components/Attribution';
import Calculator from './components/Calculator';
import Result from './components/Result';
import { useState, useRef } from 'react';
import { ReactComponent as Logo } from './images/logo.svg'

function App() {
  const [bill, setBill] = useState(0.00);
  const [people, setPeople] = useState(0);
  const [tip, setTip] = useState(0.00);

  const entryRef = useRef(null);
  const peopleRef = useRef(null);


  const tipPercentage = [
    .05,
    .10,
    .15,
    .25,
    .50
  ]

  function handleBill(e) {
    // const value = parseFloat(e.target.value)
    // console.log(value);

    setBill(e.target.value);
  }

  function handlePeople(e) {
    const value = parseFloat(e.target.value)

    setPeople(value);
  }

  function handleTip(e) {
    const value = parseFloat(e.target.value)
    console.log(`tip value: ${tip}`);
    setTip(value);
  }

  const handleReset = function () {
    const peopleMessage = document.querySelector("#input__message--people");
    const billMessage = document.querySelector("#input__message--bill");

    if (people !== 0 || bill !== 0) {
      setBill(0.00);
      setTip(0.00);
      setPeople(0);
      entryRef.current.value = { bill };
      peopleRef.current.value = { people };
      peopleMessage.classList.add("hidden")
      billMessage.classList.add("hidden")
      console.log(typeof bill);
    }
    else if (people < 1) {
      peopleMessage.classList.remove("hidden")
    }
    else if (bill === 0) {
      billMessage.classList.remove("hidden")
    }
  }


  return (
    <main>
      <Logo className="logo" />
      <div className='main__container'>
        <Calculator
          bill={bill}
          people={people}
          tip={tip}
          handleBill={handleBill}
          handleTip={handleTip}
          handlePeople={handlePeople}
          tipPercentage={tipPercentage}
          handleReset={handleReset}
          entryRef={entryRef}
          peopleRef={peopleRef}

        />
        <Result
          bill={bill}
          people={people}
          tip={tip}
          setBill={setBill}
          setPeople={setPeople}
          setTip={setTip}
          handleReset={handleReset} />
      </div>
      {/* <Attribution /> */}
    </main>
  );
}

export default App;
