import { useState } from 'react'

export default function Calculator() {
    const [bill, setBill] = useState(0.00);
    const [people, setPeople] = useState(0);
    const [tip, setTip] = useState('');

    const tipPercentage = [
        .05,
        .10,
        .15,
        .25,
        .50
    ]

    const tipButtons = tipPercentage.map(percentage => <button className="percentSelect " value={percentage} onClick={() => { setTip(percentage) }}>{`${percentage * 100}%`}</button>)

    function handleBill(e) {
        setBill(e.target.value);
        console.log(bill);
    }

    function handlePeople(e) {
        setPeople(e.target.value);
        console.log(people);

    }



    return (
        <section className="container calculator">
            <div className="input__span">
                <h3>Bill</h3>
                <span id="input__message" className="hidden">Must be a number</span>
            </div>
            <input
                id="entry"
                className="input__main"
                placeholder={bill}
                type="number"
                onChange={handleBill}
            />

            <h3>Select Tip %</h3>

            <div id="tipSelect">
                {tipButtons}
                <button className="customPercent" value="">Custom</button>
            </div>
            <input
                id="customInput"
                className="percentSelect hidden"
                type="number"
                placeholder={people}
            />

            <div className="input__span">
                <h3>Number of People</h3>
                <span id="input__message" className="hidden">Must be a number</span>
            </div>
            <input type="number" className="input__main" placeholder="0" id="people" onChange={handlePeople} />
        </section >

    )
}
