        if (isNaN(tipr)) {
            tipr = 0;
        }
        return tipr;
    };

    const totalResult = function () {

        //should be the (total bill) + (tip % of total bill) / # of people ordering
        let totlr = (billNum + (billNum * tipNum)) / peopleNum;



