export default function Result({ tip, bill, people }) {
    const tipResult = function () {
        let tipr = (bill * tip) + bill / people
        console.log(`tipr: ${tipr}, bill: ${bill}, tip: ${tip}, people: ${people}`);

        if (tipr === NaN) {
            tipr = 0
            return tipr
        }
        else {
            return tipr
        }
    }

    const totalResult = function () {
        let totlr = bill / people * tip
        console.log(`totlr: ${totlr}, bill: ${bill}, tip: ${tip}, people: ${people}`);

        if (totlr == NaN) {
            totlr = 0
            return totlr
        }
        else {
            return totlr
        }
    }


    return (
        <section className="result">
            <div className="result__total">
                <div className="result__total--units">
                    <h4>Tip Amount</h4>
                    <h5>/ person</h5>
                </div>
                <h1 className="result__total--tip">{tipResult()}</h1>
            </div>
            <div className="result__total">
                <div className="result__total--units">
                    <h4>Total</h4>
                    <h5>/ person</h5>
                </div>
                <h1 className="result__total--bill">{totalResult()}</h1>
            </div>

            <button id="reset">Reset</button>
        </section>

    )
}
